import React from "react";
import SearchBar from "./SearchBar"

class Image extends React.Component {

    state = {images : []}

    componentDidMount(){
        const API = "https://www.themealdb.com/api/json/v1/1/random.php"

        fetch(API).then(res => res.json())
        .then(data => this.setState({images: data.meals[0].strMealThumb})) 
        //Note how we updated the state property of "images" using setState here to the value 
    }

   render () {
    return (

<div>

<SearchBar submit = {this.state.images} />
</div>

    )
   }
}

export default Image;
